mapper.select:
select mem_no, mem_id, mem_pwd, mem_name, mem_birth, mem_email, mem_phone, mem_zip, mem_addr1, mem_addr2, mem_addr3, mem_rdate
from member
where mem_id = #{mem_id}

mapper.insert:
/*member - memberInsert*/
select member_seq.nextval from dual
insert into member(mem_no, mem_id, mem_pwd, mem_name, mem_birth, mem_email, mem_phone, mem_sex, mem_zip, mem_addr1, mem_addr2, mem_addr3, mem_rdate) values(#{mem_no}, #{mem_id}, #{mem_pwd}, #{mem_name}, #{mem_birth}, #{mem_email}, #{mem_phone}, #{mem_sex}, #{mem_zip}, #{mem_addr1}, #{mem_addr2}, #{mem_addr3}, sysdate)

mapper.update:
update member set

	mem_pwd=#{mem_pwd},

	mem_phone=#{mem_phone}, mem_email=#{mem_email}
	where mem_id=#{mem_id}
