mapper.select:
SELECT * FROM ( SELECT /*+ INDEX_DESC(prod prod_pk)*/ rownum rn, rowid ri,
FROM prod WHERE
pd_sex = #{pd_sex}
pd_age = 'a'
pd_age = #{pd_age}
AND pct_no = #{pct_no}
AND co_no = #{co_no}
AND (pd_name LIKE '%'||#{keyword}||'%')
AND ( SELECT DISTINCT 1 FROM prod_ins WHERE pd_no = prod.pd_no AND pcl_no = #{color} ) IS NOT NULL
AND ( SELECT DISTINCT 1 FROM prod_stock INNER JOIN prod_ins ON prod_ins.pi_no = prod_stock.pi_no WHERE pd_no = prod.pd_no AND ps_size = #{size} ) IS NOT NULL
AND pd_price * NVL((100-())/100, 1) >= #{priceBottom}
AND pd_price * NVL((100-())/100, 1) < #{priceTop}
AND rownum <= #{pageNum} * #{amount}
) prodlist WHERE rn > (#{pageNum} - 1) * #{amount}
AND ( SELECT DISTINCT 1 FROM prod_stock INNER JOIN prod_ins ON prod_stock.pi_no = prod_ins.pi_no WHERE pd_no = prodlist.pd_no ) IS NOT NULL
